mapper.insert:
INSERT
INTO party(party_no
			, party_name
		    
		    	,festival_no
		    
		    , user_id
		    , party_date
		    , party_time
		    , party_member_limit
			
				, party_image
			
			, party_detail
			, party_place
			, party_reg_date
			, delete_flag)
VALUES(seq_party_party_no.nextval
		,#{partyName:VARCHAR}
		
			,#{festival.festivalNo:NUMERIC}
		
		,#{user.userId:VARCHAR}
		,#{partyDate:DATE}
		,#{partyTime:VARCHAR}
		,#{partyMemberLimit:NUMERIC}
		
			,#{partyImage:VARCHAR}
		
		,#{partyDetail:VARCHAR}
		,#{partyPlace:VARCHAR}
		,SYSDATE
		,#{deleteFlag:VARCHAR})

mapper.update:
UPDATE party
festival_no = #{festival.festivalNo}, party_name = #{partyName}, party_date = #{partyDate}, party_time = #{partyTime}, party_detail = #{partyDetail}, party_member_limit = #{partyMemberLimit}, party_place = #{partyPlace}, party_image = #{partyImage}
WHERE party_no = #{partyNo}

mapper.select:
SELECT COUNT(*) FROM( SELECT * FROM ( SELECT inner_table.*, ROWNUM AS row_seq FROM party_member pm, (SELECT p.party_no AS party_no, p.party_image, f.festival_no, f.festival_name, p.party_name, u.user_id, u.nickname, p.party_date AS party_date, p.party_time, p.party_place FROM party p, festival f, users u
p.festival_no = f.festival_no(+) AND p.user_id = u.user_id AND p.delete_flag IS NULL
AND p.party_name LIKE '%${search.searchKeyword}%'
AND p.party_date < SYSDATE
AND p.party_date < SYSDATE AND p.party_name LIKE '%${search.searchKeyword}%'
AND p.party_date >= SYSDATE
AND p.party_date >= SYSDATE AND p.party_name LIKE '%${search.searchKeyword}%'
) inner_table
pm.party_no = inner_table.party_no AND pm.user_id = #{userId:VARCHAR} AND ROWNUM <= #{search.endRowNum:NUMERIC}
ORDER BY inner_table.party_date ASC)
row_seq BETWEEN #{search.startRowNum:NUMERIC} AND #{search.endRowNum:NUMERIC}
) countTable

mapper.insert:
INSERT
INTO party_member(member_no, party_no, user_id, role, age, gender)
VALUES(seq_party_member_member_no.nextval
		,#{party.partyNo:NUMERIC}
		,#{user.userId:VARCHAR}
		,#{role:VARCHAR}
		,#{user.age:NUMERIC}
		,#{user.gender:VARCHAR})

mapper.delete:
DELETE
FROM party_member
WHERE party_no = #{partyNo}
AND user_id = #{userId}

mapper.update:
UPDATE party_member

	delete_flag = 'del' 

WHERE party_no = #{partyNo}
AND user_id = #{userId}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT p.party_no, p.party_name, u.user_id, u.nickname, u.profile_image, pm.role, pm.age, pm.gender FROM party_member pm, party p, users u
pm.user_id = u.user_id AND pm.party_no = p.party_no AND pm.party_no = #{partyNo}
AND u.nickname LIKE '%${search.searchKeyword}%'
ORDER BY pm.role DESC ) inner_table)
ORDER BY pm.role DESC ) inner_table WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT COUNT(*) FROM( SELECT * FROM party_member pm, users u
pm.user_id = u.user_id AND pm.party_no = #{partyNo}
AND u.nickname LIKE '%${search.searchKeyword}%'
) countTable
