mapper.insert:
INSERT INTO ZZIM (

ZZIM_NO,
USER_ID,
FESTIVAL_NO

)

VALUES (

SEQ_ZZIM_ZZIM_NO.NEXTVAL,
#{userId:VARCHAR},
#{festivalNo:NUMERIC}

)

mapper.delete:
DELETE FROM ZZIM 
WHERE USER_ID = #{userId:VARCHAR}
AND FESTIVAL_NO = #{festivalNo:NUMERIC}

)

mapper.select:
SELECT * FROM ZZIM
WHERE USER_ID = #{userId:VARCHAR}
AND FESTIVAL_NO = #{festivalNo:NUMERIC}
