mapper.update:
UPDATE festival
FESTIVAL_NO = #{festivalNo:NUMERIC}, FESTIVAL_NAME = #{festivalName:VARCHAR}, CONTENT_TYPE_ID = #{contentTypeId:VARCHAR}, READ_COUNT = #{readCount:NUMERIC}, FESTIVAL_LONGITUDE = #{festivalLongitude:VARCHAR}, FESTIVAL_LATITUDE = #{festivalLatitude:VARCHAR}, ADDR = #{addr:VARCHAR}, AREA_CODE = #{areaCode:VARCHAR}, SIGUNGU_CODE = #{sigunguCode:VARCHAR}, ORG_PHONE = #{orgPhone:VARCHAR}, START_DATE = #{startDate:VARCHAR}, END_DATE = #{endDate:VARCHAR}, FESTIVAL_DETAIL = #{festivalDetail:VARCHAR}, FESTIVAL_OVERVIEW = #{festivalOverview:VARCHAR}, AGE_LIMIT = #{ageLimit:VARCHAR}, BOOKING_PLACE = #{bookingPlace:VARCHAR}, DISCOUNT = #{discount:VARCHAR}, PLAY_TIME = #{playTime:VARCHAR}, SPEND_TIME_FESTIVAL = #{spendTimeFestival:VARCHAR}, SUB_EVENT = #{subEvent:VARCHAR}, PROGRAM = #{program:VARCHAR}, USE_TIME_FESTIVAL = #{useTimeFestival:VARCHAR}, DELETE_FLAG = #{deleteFlag:VARCHAR}, FESTIVAL_IMAGE = #{festivalImage:VARCHAR}, REG_DATE = SYSDATE
WHERE FESTIVAL_NO =#{festivalNo:NUMERIC}

mapper.insert:
INSERT INTO festival (
