)
VALUES (
#{festivalNo:NUMERIC}, #{festivalName:VARCHAR}, #{contentTypeId:VARCHAR}, #{readCount:NUMERIC}, #{festivalLongitude:VARCHAR}, #{festivalLatitude:VARCHAR}, #{addr:VARCHAR}, #{areaCode:VARCHAR}, #{sigunguCode:VARCHAR}, #{orgPhone:VARCHAR}, #{startDate:VARCHAR}, #{endDate:VARCHAR}, #{festivalDetail:VARCHAR}, #{festivalOverview:VARCHAR}, #{ageLimit:VARCHAR}, #{bookingPlace:VARCHAR}, #{discount:VARCHAR}, #{playTime:VARCHAR}, #{spendTimeFestival:VARCHAR}, #{subEvent:VARCHAR}, #{program:VARCHAR}, #{useTimeFestival:VARCHAR}, #{deleteFlag:VARCHAR}, #{festivalImage:VARCHAR}, SYSDATE
