mapper.select:
SELECT vt.purchase_date AS stat_date, vt.purchase_price AS total_price, vt.purchase_count AS total_count, st.stat_flag AS stat_flag FROM ( SELECT to_char(purchase_date, 'yyyy-mm') AS purchase_date, SUM(purchase_price) AS purchase_price, SUM(purchase_count) AS purchase_count FROM purchase
to_char(purchase_date, 'yyyy') = to_char(SYSDATE, 'yyyy')
AND to_char(purchase_date,'yyyy-mm') = #{startDate}
AND to_char(purchase_date,'yyyy-mm') BETWEEN #{startDate} AND #{endDate}
GROUP BY to_char(purchase_date, 'yyyy-mm') ORDER BY purchase_date ) vt, ( SELECT '2' AS stat_flag FROM dual ) st
